from crewai.tools import BaseTool
from pydantic import BaseModel, Field
import base64
import requests
import logging
from typing import Type, Any, List, Dict

# Setup logging for the GitHub File Reader Tool
logging.basicConfig(
    level=logging.INFO,
    format='%(asctime)s - %(name)s - %(levelname)s - %(message)s',
    filename='github_file_reader.log'
)
logger = logging.getLogger('GitHubFileReaderTool')

class GitHubFileReaderSchema(BaseModel):
    """Input schema for the GitHubFileReaderTool."""
    repo: str = Field(..., description="GitHub repository in the format 'owner/repo'")
    file_paths: List[str] = Field(..., description="List of file paths in the repository")
    branch: str = Field(..., description="Branch name to read the files from (e.g., 'main')")
    token: str = Field(..., description="GitHub personal access token for authorization")

class GitHubFileReaderTool(BaseTool):
    name: str = "GitHub File Reader Tool"
    description: str = "Reads multiple files from a GitHub repository based on user inputs."
    args_schema: Type[BaseModel] = GitHubFileReaderSchema

    api_url_template: str = "https://api.github.com/repos/{repo}/contents/{file_path}"

    def fetch_file_from_github(self, repo: str, file_path: str, branch: str, token: str) -> str:
        """Fetches a file content from GitHub."""
        url = self.api_url_template.format(repo=repo, file_path=file_path)
        headers = {
            "Authorization": f"token {token}",
            "Accept": "application/vnd.github.v3+json"
        }
        params = {"ref": branch}

        try:
            logger.info(f"Fetching file '{file_path}' from repo '{repo}' on branch '{branch}'")
            response = requests.get(url, headers=headers, params=params)
            response.raise_for_status()

            file_data = response.json()
            if "content" not in file_data:
                raise ValueError(f"❌ Error: Path '{file_path}' might be a directory or missing content.")

            decoded_content = base64.b64decode(file_data['content']).decode('utf-8')
            logger.info(f"✅ Successfully fetched file '{file_path}'.")
            return decoded_content

        except Exception as e:
            logger.error(f"Failed to fetch file '{file_path}': {str(e)}", exc_info=True)
            raise

    def _run(self, repo: str, file_paths: List[str], branch: str, token: str) -> Dict[str, Any]:
        """Main execution logic."""
        all_files_content = {}
        for file_path in file_paths:
            try:
                content = self.fetch_file_from_github(repo, file_path, branch, token)
                all_files_content[file_path] = {"status": "success", "content": content}
            except Exception as e:
                all_files_content[file_path] = {"status": "error", "message": str(e)}

        return all_files_content


# Example Usage
if __name__ == '__main__':
    github_token = "YOUR_GITHUB_TOKEN"
    github_repo = "owner/repository-name"
    github_branch = "main"
    github_files = [
        "path/to/file1.txt",
        "path/to/file2.sql",
        "path/to/file3.json"
    ]

    if github_token == "YOUR_GITHUB_TOKEN":
        print("⚠️ Please replace the placeholder values before running.")
    else:
        reader_tool = GitHubFileReaderTool()
        result = reader_tool.run(
            repo=github_repo,
            file_paths=github_files,
            branch=github_branch,
            token=github_token
        )

        for file, details in result.items():
            print(f"\nFile: {file}")
            if details['status'] == 'success':
                print(f"Content:\n{details['content'][:200]}...")  # print first 200 characters
            else:
                print(f"Error: {details['message']}")

